pragma solidity ^0.5.0;

import "./util/SafeMath.sol";
import "./util/Ownable.sol";

/**
 *@title Factory contract for creating new payment channels
 */
contract ScaaSSC is Ownable {

    using SafeMath for uint256;

    enum ChannelState { INIT, OPENED, CLOSED }
    uint public oraclePercantage_ = 10; // trenutno 10 %
    uint public minStake = 0.005 ether; // minimalno lahko greš v kanal z 0.005 ethra

    struct Channel {
        address payable oracleAddress;
        string ipnsId;
        address a;
        address b;
        mapping(address=>uint) balances;
        ChannelState currentState;
    }

    mapping(uint => Channel) public channels;

    /// ---------- USER-CENTRIC FUNCTIONS ---------- /// 1,"0xdd870fa1b7c4700f2bd7f44238821c26f7392148",1,"0x4b0897b0513fdc7c541b6d9d7e929c4e5364d2db"
    modifier checkNewChannel(uint _channelId, address payable _oracleAddress, string memory _ipnsId, address _b) {
        require(_channelId != 0 && _oracleAddress != address(0x0) && bytes(_ipnsId).length != 0 && _b != address(0x0));
        require(bytes(channels[_channelId].ipnsId).length == 0); // kanal z podanim IDjem se ne obstaja
        require(minStake <= msg.value);
        _;
    }

    modifier checkJoinChannel(uint _channelId) {
        require(_channelId != 0);
        require(channels[_channelId].balances[channels[_channelId].a] == msg.value - (msg.value.div(100).mul(oraclePercantage_)));
        require(channels[_channelId].b == msg.sender);
        require(channels[_channelId].currentState == ChannelState.INIT);
        _;
    }

    modifier checkExitChannel(uint _channelId) {
        require(_channelId != 0);
        require(channels[_channelId].a == msg.sender);
        require(channels[_channelId].currentState == ChannelState.INIT);
        _;
    }

    modifier checkRefundChannel(uint _channelId) {
        require(_channelId != 0);
        require(channels[_channelId].currentState == ChannelState.CLOSED);
        require(channels[_channelId].balances[msg.sender] != 0);
        _;
    }

    function newChannel(uint _channelId, address payable _oracleAddress, string memory _ipnsId, address _b) checkNewChannel(_channelId, _oracleAddress, _ipnsId, _b) public payable {
        channels[_channelId]  =  Channel(_oracleAddress, _ipnsId, msg.sender, _b, ChannelState.INIT);
        Channel storage _temp = channels[_channelId];
        _temp.oracleAddress.transfer(msg.value.div(100).mul(oraclePercantage_)); // 10% od gre k oraclu
        _temp.balances[msg.sender] = msg.value - (msg.value.div(100).mul(oraclePercantage_));
        _temp.balances[_b] = msg.value - (msg.value.div(100).mul(oraclePercantage_));
    }

    function joinChannel(uint _channelId) checkJoinChannel(_channelId) external payable {
        Channel storage _temp = channels[_channelId];
        _temp.balances[msg.sender] = msg.value - (msg.value.div(100).mul(oraclePercantage_)); // 90% gre za kanal
        _temp.oracleAddress.transfer(msg.value.div(100).mul(oraclePercantage_)); // 10% od gre k oraclu
        _temp.currentState = ChannelState.OPENED;
    }

    function exitChannel(uint _channelId) checkExitChannel(_channelId) external {
        Channel storage _temp = channels[_channelId];
        address(uint160(msg.sender)).transfer(_temp.balances[msg.sender]);
        _temp.currentState = ChannelState.CLOSED;
        delete channels[_channelId];
    }

    function refund(uint _channelId) checkRefundChannel(_channelId) external {
        Channel storage _temp = channels[_channelId];
        uint refundValue = _temp.balances[msg.sender];
        _temp.balances[msg.sender] = 0;
        msg.sender.transfer(refundValue);
        if(_temp.balances[_temp.a] == 0 && _temp.balances[_temp.b] == 0) {
            delete channels[_channelId];
        }
    }

    /// ---------- SYSTEM-CENTRIC FUNCTIONS ---------- ///
    modifier checkCloseChannel(uint _channelId) {
        require(_channelId != 0);
        require(channels[_channelId].currentState == ChannelState.OPENED);
        require(channels[_channelId].oracleAddress == msg.sender);
        _;
    }

    function getChannelInfo(uint _channelId) external view returns(address, string memory, address, address, uint, uint, ChannelState){
        Channel storage _temp = channels[_channelId];
        return (_temp.oracleAddress, _temp.ipnsId, _temp.a, _temp.b, _temp.balances[_temp.a], _temp.balances[_temp.b], _temp.currentState);
    }

    function closeChannel(uint _channelId, uint balanceAddr1_, uint balanceAddr2_, bytes calldata _ipfsContent, bytes calldata _signatureOracle, bytes calldata _signatureLast) checkCloseChannel(_channelId) external {
        Channel storage _temp = channels[_channelId];
        bytes32 hash = toEthPersonalSignedMessageHash(bytes(_ipfsContent));
        require(_temp.oracleAddress == address(uint160(recoverAddress(hash, _signatureOracle))));
        require(_temp.a == address(uint160(recoverAddress(hash, _signatureLast))) || _temp.b == address(uint160(recoverAddress(hash, _signatureLast))));
        _temp.balances[_temp.a] = balanceAddr1_;
        _temp.balances[_temp.b] = balanceAddr2_;
        _temp.currentState = ChannelState.CLOSED;
    }

    /// ---------- UTIL FUNCTIONS ---------- ///
    function toEthPersonalSignedMessageHash(
        bytes memory _msg
    )
        pure
        public
        returns (bytes32 signHash)
    {
        signHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n", uint2str(_msg.length), _msg));
    }

    function uint2str(uint _i) internal pure returns (string memory _uintAsString) {
        if (_i == 0) {
            return "0";
        }
        uint j = _i;
        uint len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint k = len - 1;
        while (_i != 0) {
            bstr[k--] = byte(uint8(48 + _i % 10));
            _i /= 10;
        }
        return string(bstr);
    }


    function recoverAddress(
        bytes32 _hash,
        bytes memory _signatures
    )
        pure
        internal
        returns (address)
    {
        uint8 v;
        bytes32 r;
        bytes32 s;

         assembly {
            r := mload(add(_signatures, 0x20))
            s := mload(add(_signatures, 0x40))
            v := byte(0, mload(add(_signatures, 0x60)))
        }

        if (v < 27) v += 27;

        require(v == 27 || v == 28);
        return ecrecover(_hash, v, r, s);
    }

}

